import java.util.Scanner;import java.util.Random;// Methods including dice rom Lab part Bpublic class Lab10MethodsSelfB {     static int rollDie(int sides, Random rand) {        return Math.abs(rand.nextInt()) % sides + 1;    }    public static int rollDice(int count, int numSides, Random rand) {        int sum = 0, d;        for(int i = 0; i < count; i++) {            d= rollDie(numSides, rand);            System.out.println("\tRolled a " + d);            sum += d;        }        return sum;    }//    publicstatic int getPositiveNumberfromUser(String message, Scanner in) {//        in//    }t input;//        do {//            System.out.print("Enter a positive number: ");//            input = in.nextInt();//        } while(input <= 0);//        return input;    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int sides, numDice, numRolls, inputResult;        sides = getPositiveNumberfromUser("How many sides should the dice have?: ", in);        numDice = getPositiveNumberfromUser("How many dice should I roll each time?: ", in);        numRolls = getPositiveNumberfromUser("How many times should I roll?" , in);        Random rand = new Random();        for(int i = 0; i < numRolls; i++) {            inputResult = rollDice(numDice, sides, rand);            System.out.println("Roll " + (i + 1) + " was " + inputResult);        }    }    public static int getPositiveNumberfromUser(String message, Scanner in) {        int input;        do {            System.out.print(message + " ");            input = in.nextInt();        } while(input <= 0);        return input;    }}